queries.query:
from(bucket: "meross_ingress")
  |> range(start: -30d)
  |> filter(fn: (r) => r["_measurement"] == "meross_data")
  |> filter(fn: (r) => r["_field"] == "power")
  |> filter(fn: (r) => r["device"] == "Otti-Schreibtisch")
  |> aggregateWindow(every: 20s, fn: sum, createEmpty: false)
  |> map(fn: (r) => ({
        r with _value: r._value / 6.0
      }))
  |> yield(name: "sum")

from(bucket: "meross_ingress")
  |> range(start: -1d)
  |> filter(fn: (r) => r["_measurement"] == "meross_data")
  |> filter(fn: (r) => r["_field"] == "power")
  |> filter(fn: (r) => r["device"] == "Otti-Schreibtisch")
  |> aggregateWindow(every: 20s, fn: sum, createEmpty: false)
  |> map(fn: (r) => ({
        r with _value: r._value / 104.0
      }))
  |> yield(name: "sum")
